(4, 'Biển nào (đặt trước ngã ba, ngã tư) cho phép xe được rẽ sang hướng khác?', 'Biển 1.', 'Biển 2.', 'Không biển nào.', NULL, 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/147.png'), 'Biển 1: R.301a “Hướng đi phải theo” chỉ được đi thẳng đặt trước ngã ba, ngã tư; Biển 2: R.301h “Hướng đi phải theo” chỉ được rẽ trái hoặc rẽ phải đặt sau ngã ba, ngã tư; Nên đáp án đúng là không có biển nào.', FALSE),
(4, 'Biển nào báo hiệu “Hướng đi thẳng phải theo”?', 'Biển 1.', 'Biển 2.', NULL, NULL, 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/148.png'), 'Biển 1: R.301a “Hướng đi phải theo” chỉ được đi thẳng đặt trước ngã ba, ngã tư; Biển 2: I.407a “Đường một chiều” không liên quan hướng đi phải theo. Nên Biển 1 là đáp án đúng.', FALSE),
(4, 'Biển nào báo hiệu “Đường một chiều”?', 'Biển 1.', 'Biển 2.', 'Cả hai biển.', NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/149.png'), 'Biển 1: R.301a “Hướng đi phải theo” chỉ được đi thẳng đặt trước ngã ba, ngã tư; Biển 2: I.407a “Đường một chiều” không liên quan hướng đi phải theo. Nên biển 2 là đáp án đúng.', FALSE),
(4, 'Trong các biển dưới đây biển nào là biển “Hết tốc độ tối đa cho phép”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', 'Cả ba biển.', 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/150.png'), 'Biển 1: DP.134 “Hết hạn chế tốc độ tối đa”; Biển 2: DP.135 “Hết mọi lệnh cấm”; Biển 3: R.307 “Hết hạn chế tốc độ tối thiểu”. Nên biển 1 là biển “Hết hạn chế tốc độ tối đa”. Chú ý câu này hỏi ý nghĩa của biển.', FALSE),
(4, 'Hiệu lực của biển “Tốc độ tối đa cho phép” hết tác dụng khi gặp biển nào dưới đây?', 'Biển 1.', 'Biển 2.', 'Biển 3.', 'Biển 1 và 2.', 4, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/151.png'), 'Biển 1: DP.134 “Hết hạn chế tốc độ tối đa”; Biển 2: DP.135 “Hết mọi lệnh cấm”; Biển 3: R.307 “Hết hạn chế tốc độ tối thiểu”. Nên Biển 1 và Biển 2 là biển “Hết hạn chế tốc độ tối đa”. Chú ý câu hỏi các biển hết hạn chế, không phải hỏi ý nghĩa của biển.', FALSE),
(4, 'Trong các biển dưới đây biển nào là biển “Hết tốc độ tối thiểu”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', 'Cả ba biển.', 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/152.png'), 'Biển 1: DP.134 “Hết hạn chế tốc độ tối đa”; Biển 2: DP.135 “Hết mọi lệnh cấm”; Biển 3: R.307 “Hết hạn chế tốc độ tối thiểu”. Nên Biển 3 là biển “Hết hạn chế tốc độ tối thiểu”. Câu này hỏi ý nghĩa của biển.', FALSE),
(4, 'Biển nào dưới đây báo hiệu hết cấm vượt?', 'Biển 1.', 'Biển 2.', 'Biển 3.', 'Biển 2 và 3.', 4, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/153.png'), 'Biển 1: DP.134 “Hết hạn chế tốc độ tối đa”; Biển 2: DP.135 “Hết mọi lệnh cấm”; Biển 3: DP.133 “Hết cấm vượt”. Nên đáp án đúng phải là Biển 2 và Biển 3.', FALSE),
(4, 'Trong các biển dưới đây biển nào là biển “Hết mọi lệnh cấm”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', 'Cả ba biển.', 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/154.png'), 'Biển 1: DP.134 “Hết hạn chế tốc độ tối đa”; Biển 2: DP.135 “Hết mọi lệnh cấm”; Biển 3: R.307 “Hết hạn chế tốc độ tối thiểu”.', FALSE),
(4, 'Biển nào chỉ dẫn cho người đi bộ sử dụng cầu vượt qua đường?', 'Biển 1.', 'Biển 2.', 'Cả hai biển.', 'Không biển nào.', 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/155.png'), 'Biển 1: I.424a “Cầu vượt qua đường cho người đi bộ”; Biển 2: I.424c “Hầm chui qua đường cho người đi bộ”. Nên đáp án đúng là biển 1.', FALSE),
(4, 'Biển nào chỉ dẫn cho người đi bộ sử dụng hầm chui qua đường?', 'Biển 1.', 'Biển 2.', 'Cả hai biển.', 'Không biển nào.', 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/156.png'), 'Biển 1: I.424b “Cầu vượt qua đường cho người đi bộ”; Biển 2: I.424d “Hầm chui qua đường cho người đi bộ”. Nên đáp án đúng là biển 2.', FALSE),
(4, 'Biển nào báo hiệu “Nơi đỗ xe dành cho người khuyết tật”?', 'Biển 1.', 'Biển 2.', 'Biển 3.', NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/157.png'), NULL, FALSE),
(4, 'Gặp biển báo này, người tham gia giao thông phải xử lý như thế nào?', 'Dừng xe tại khu vực có trạm Cảnh sát giao thông.', 'Tiếp tục lưu thông với tốc độ bình thường.', 'Phải giảm tốc độ đến mức an toàn và không được vượt khi đi qua khu vực này.', NULL, 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/158.png'), 'Biển I.436 “Trạm cảnh sát giao thông”. Các phương tiện phải giảm tốc độ đến mức an toàn và không được vượt khi đi qua khu vực này.', FALSE),
(4, 'Biển số 1 có ý nghĩa gì?', 'Đi thẳng hoặc rẽ trái trên cầu vượt.', 'Đi thẳng hoặc rẽ phải trên cầu vượt.', 'Báo hiệu cầu vượt liên thông.', NULL, 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/159.png'), NULL, FALSE),
(4, 'Vạch kẻ đường nào dưới đây là vạch phân chia các làn xe cùng chiều?', 'Vạch 1.', 'Vạch 2.', 'Vạch 3.', 'Vạch 1 và 2.', 4, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/160.png'), NULL, FALSE),
(4, 'Vạch kẻ đường nào dưới đây là vạch phân chia hai chiều xe chạy (vạch tim đường), xe không được lấn làn, không được đè lên vạch?', 'Vạch 1.', 'Vạch 2.', 'Vạch 3.', 'Cả 3 vạch.', 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/161.png'), NULL, FALSE),
(4, 'Vạch kẻ đường nào dưới đây là vạch phân chia hai chiều xe chạy (vạch tim đường)?', 'Vạch 1.', 'Vạch 2.', 'Vạch 3.', 'Vạch 1 và 3.', 4, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/162.png'), NULL, FALSE),
(4, 'Các vạch dưới đây có tác dụng gì?', 'Phân chia hai chiều xe chạy ngược chiều nhau.', 'Phân chia các làn xe chạy cùng chiều nhau.', NULL, NULL, 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/163.png'), NULL, FALSE),
(4, 'Khi gặp vạch kẻ đường nào các xe được phép đè vạch?', 'Vạch 1.', 'Vạch 2.', 'Vạch 3.', 'Vạch 1 và vạch 3.', 4, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/164.png'), NULL, FALSE),
(4, 'Vạch dưới đây có ý nghĩa gì?', 'Vị trí dừng xe của các phương tiện vận tải hành khách công cộng.', 'Báo cho người điều khiển được dừng phương tiện trong phạm vi phần mặt đường có bố trí vạch để tránh ùn tắc giao thông.', 'Dùng để xác định vị trí giữa các phương tiện trên đường.', NULL, 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/165.png'), 'Vạch 9.2: Vạch quy định vị trí dừng đỗ của phương tiện giao thông công cộng trên đường.', FALSE);
INSERT INTO group_question (name) VALUES ('Sa hình');
INSERT INTO question (group_id, question, option1, option2, option3, option4, answer, image, explain_question, failing_score) VALUES
(5, 'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?', 'Xe tải, xe khách, xe con, mô tô.', 'Xe tải, mô tô, xe khách, xe con.', 'Xe khách, xe tải, xe con, mô tô.', 'Mô tô, xe khách, xe tải, xe con.', 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/166.png'), 'Thứ tự ưu tiên: Xe ưu tiên - Đường ưu tiên - Đường cùng cấp theo thứ tự bên phải trống – rẽ phải – đi thẳng – rẽ trái. 1. Xe tải: Đường ưu tiên và đi thẳng; 2. Mô tô: Đường ưu tiên và rẽ trái; 3. Xe khách: Đường không ưu tiên, đi thẳng. 4. Xe con: Đường không ưu tiên, rẽ trái.', FALSE),
(5, 'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?', 'Xe tải, xe con, mô tô.', 'Xe con, xe tải, mô tô.', 'Mô tô, xe con, xe tải.', 'Xe con, mô tô, xe tải.', 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/167.png'), 'Thứ tự ưu tiên: Xe ưu tiên - Đường ưu tiên - Đường cùng cấp theo thứ tự bên phải trống – rẽ phải – đi thẳng – rẽ trái. 1. Xe mô tô: Bên phải trống; 2. Xe con: Đi thẳng; 3. Xe tải: Rẽ trái.', FALSE),
(5, 'Trường hợp này xe nào được quyền đi trước?', 'Mô tô.', 'Xe con.', NULL, NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/168.png'), 'Xe con được đi trước vì mô tô có biển STOP trước mặt.', FALSE),
(5, 'Xe nào được quyền đi trước trong trường hợp này?', 'Mô tô.', 'Xe cứu thương.', NULL, NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/169.png'), 'Xe ưu tiên đi trước.', FALSE),
(5, 'Theo tín hiệu đèn, xe nào được phép đi?', 'Xe con và xe khách.', 'Mô tô.', NULL, NULL, 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/170.png'), 'Xe con và xe khách đang ở làn đường có tín hiệu đèn xanh nên được phép đi.', FALSE),
(5, 'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?', 'Xe khách, xe tải, mô tô.', 'Xe tải, xe con, mô tô.', 'Xe khách, xe con, mô tô.', NULL, 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/171.png'), '1. Xe khách: Sai làn, đi thẳng đèn đang đỏ; 2. Xe tải: Sai làn: Đi trên làn đi thẳng trong khi thực hiện rẽ trái. 3. Xe mô tô: Sai làn: Đi trên làn rẽ trái trong khi thực hiện rẽ phải. Nên đáp án đúng là xe khách, xe tải và xe mô tô đều vi phạm quy tắc giao thông.', FALSE),
(5, 'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?', 'Xe khách, xe tải, mô tô, xe con.', 'Xe con, xe khách, xe tải, mô tô.', 'Mô tô, xe tải, xe khách, xe con.', 'Mô tô, xe tải, xe con, xe khách.', 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/172.png'), 'Giao nhau cùng cấp có vòng xuyến: Chưa vào vòng xuyến thì ưu tiên xe bên phải; đã vào vòng xuyến ưu tiên xe từ bên trái tới. Toàn bộ các xe chưa vào vòng xuyến nên ưu tiên xe bên phải. Thứ tự sẽ là mô tô, xe tải, xe khách, xe con.', FALSE),
(5, 'Trong trường hợp này xe nào đỗ vi phạm quy tắc giao thông?', 'Xe tải.', 'Xe con và mô tô.', 'Cả ba xe.', 'Xe con và xe tải.', 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/173.png'), 'Biển cấm đỗ xe tải (ở biển phụ) nên chỉ có xe tải vi phạm.', FALSE),
(5, 'Theo hướng mũi tên, những hướng nào xe gắn máy đi được?', 'Cả ba hướng.', 'Chỉ hướng 1 và 3.', 'Chỉ hướng 1.', NULL, 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/174.png'), 'Bạn lưu ý câu hỏi này về hướng đi của xe GẮN MÁY. Hướng 1 và 3 chắn chắn là đi được rồi. Với hướng 2, biển báo cấm mô tô đi vào chỉ có tác dụng với xe mô tô nên xe gắn máy không có hiệu lực. Do đó vẫn đi vào bình thường.', FALSE),
(5, 'Xe nào đỗ vi phạm quy tắc giao thông?', 'Cả hai xe.', 'Không xe nào vi phạm.', 'Chỉ xe mô tô vi phạm.', 'Chỉ xe tải vi phạm.', 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/175.png'), 'Biển cấm đỗ xe ở cả phía trước và phía sau biển (ở biển báo phụ) nên cả 2 xe vi phạm.', FALSE),
(5, 'Xe nào đỗ vi phạm quy tắc giao thông?', 'Chỉ mô tô.', 'Chỉ xe tải.', 'Cả ba xe.', 'Chỉ mô tô và xe tải.', 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/176.png'), 'Xe tải đậu trái đường; Xe con và mô tô đậu xe đè vạch nên cả 3 xe vi phạm.', FALSE),
(5, 'Xe tải kéo mô tô ba bánh như hình này có đúng quy tắc giao thông không?', 'Đúng.', 'Không đúng.', NULL, NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/177.png'), 'Xe tải kéo xe mô tô 3 bánh như trên hình là không đúng vì đi vào đường có biển P.108 “Cấm ô tô kéo moóc” kể cả xe máy, xe ô tô khách kéo theo rơ moóc đi qua.', FALSE),
(5, 'Xe nào được quyền đi trước trong trường hợp này?', 'Xe con.', 'Xe mô tô.', NULL, NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/178.png'), 'Cả 2 xe đều gặp đèn xanh nên áp dụng quy tắc đường cùng cấp: Bên phải trống – Rẽ phải – Đi thẳng – Rẽ trái. Nên đáp án đúng là xe mô tô rẽ phải được quyền đi trước. Xe con rẽ trái phải nhường đường.', FALSE),
(5, 'Thứ tự các xe đi như thế nào là đúng quy tắc giao thông?', 'Xe con (A), mô tô, xe con (B), xe đạp.', 'Xe con (B), xe đạp, mô tô, xe con (A).', 'Xe con (A), xe con (B), mô tô + xe đạp.', 'Mô tô + xe đạp, xe con (A), xe con (B).', 4, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/179.png'), 'Thứ tự ưu tiên đường cùng cấp: Xe ưu tiên – Đường ưu tiên – Bên phải trống – Rẽ phải – Đi thẳng – Rẽ trái.', FALSE),
(5, 'Xe nào được quyền đi trước trong trường hợp này?', 'Mô tô.', 'Xe con.', NULL, NULL, 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/180.png'), 'Thứ tự ưu tiên: Xe ưu tiên – Đường ưu tiên – Bên phải trống – Rẽ trái – Đi thẳng – Rẽ trái. Xe mô tô rẽ trái từ đường ưu tiên vào đường ưu tiên (theo biển báo và biển phụ) nên được quyền đi trước.', FALSE),
(5, 'Xe nào vi phạm quy tắc giao thông?', 'Xe khách.', 'Mô tô.', 'Xe con.', 'Xe con và mô tô.', 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/181.png'), 'Xe con quay đầu đè vạch liền trên đường nên vi phạm.', FALSE),
(5, 'Các xe đi như thế nào là đúng quy tắc giao thông?', 'Các xe ở phía tay phải và tay trái của người điều khiển được phép đi thẳng.', 'Cho phép các xe ở mọi hướng được rẽ phải.', 'Tất cả các xe phải dừng lại trước ngã tư, trừ những xe đã ở trong ngã tư được phép tiếp tục đi.', NULL, 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/182.png'), 'Giơ tay thẳng đứng: Tất cả dừng, trừ xe đã ở trong ngã tư được phép đi; Giang tay ngang: Trái phải đi; Trước sau dừng; Tay phải giơ trước: sau, phải dừng, trước rẽ trái, trái đi các hướng, người đi bộ qua đường đi sau người điều khiển.', FALSE),
(5, 'Theo hướng mũi tên, xe nào được phép đi?', 'Mô tô, xe con.', 'Xe con, xe tải.', 'Mô tô, xe tải.', 'Cả ba xe.', 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/183.png'), 'Giang ngang tay: Trái phải đi; Trước sau dừng. Xe mô tô và xe tải được phép đi là đáp án đúng.', FALSE),
(5, 'Trong hình dưới đây, xe nào chấp hành đúng quy tắc giao thông?', 'Chỉ xe khách, mô tô.', 'Tất cả các loại xe trên.', 'Không xe nào chấp hành đúng quy tắc giao thông.', NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/184.png'), 'Xe khách chờ đèn đỏ rẽ trái. Xe tải đi thẳng và rẽ phải theo tín hiệu đèn xanh. Xe con đi thẳng và rẽ phải theo tín hiệu đèn xanh. Xe mô tô chờ đèn đỏ rẽ trái. Nên tất cả xe đều chấp hành.', FALSE),
(5, 'Theo hướng mũi tên, những hướng nào xe mô tô được phép đi?', 'Cả ba hướng.', 'Hướng 1 và 2.', 'Hướng 1 và 3.', 'Hướng 2 và 3.', 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/185.png'), 'Hướng 2 có biển số P.104 “Cấm mô tô”; Hướng 3 Biển số P.103a “Cấm ô tô” nhưng không cấm mô tô. Nên đáp án đúng là hướng 1 và hướng 3.', FALSE),
(5, 'Trong trường hợp này, thứ tự xe đi như thế nào là đúng quy tắc giao thông?', 'Xe công an, xe quân sự, xe con + mô tô.', 'Xe quân sự, xe công an, xe con + mô tô.', 'Xe mô tô + xe con, xe quân sự, xe công an.', NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/186.png'), 'Thứ tự ưu tiên: Xe ưu tiên – Đường ưu tiên – Bên phải trống – Rẽ phải – Đi thẳng – Rẽ trái. 1. Xe quân sự: Xe ưu tiên cùng cấp xe công an nhưng đi thẳng; 2. Xe công an: Xe ưu tiên; rẽ trái; 3. Xe con và xe mô tô: Cùng đi thẳng.', FALSE),
(5, 'Trong hình dưới, những xe nào vi phạm quy tắc giao thông?', 'Xe con (E), mô tô (C).', 'Xe tải (A), mô tô (D).', 'Xe khách (B), mô tô (C).', 'Xe khách (B), mô tô (D).', 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/187.png'), 'Xe mô tô C chạy vào làn dành riêng xe ô tô: Vi phạm; Xe con E chạy vào làn dành riêng xe máy: Vi phạm.', FALSE),
(5, 'Trong hình dưới, những xe nào vi phạm quy tắc giao thông?', 'Xe con (B), mô tô (C).', 'Xe con (A), mô tô (C).', 'Xe con (E), mô tô (D).', 'Tất cả các loại xe trên.', 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/188.png'), 'Nhìn vào bảng chỉ dẫn loại phương tiện theo làn đường: Xe con (E) đi trên làn dành riêng cho mô tô nên vi phạm; Xe mô tô (D) chạy trên làn dành riêng cho ô tô nên cũng vi phạm.', FALSE),
(5, 'Bạn có được phép vượt xe mô tô phía trước không?', 'Cho phép.', 'Không được vượt.', NULL, NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/189.png'), 'Tại nơi giao nhau, trên đoạn đường có làn đường dành cho người đi bộ cắt ngang qua thì không được phép vượt.', FALSE),
(5, 'Theo tín hiệu đèn của xe cơ giới, xe nào vi phạm quy tắc giao thông?', 'Xe mô tô.', 'Xe ô tô con.', 'Không xe nào vi phạm.', 'Cả hai xe.', 4, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/190.png'), 'Biển số R.301a chỉ cho phép các phương tiện đi thẳng trong khi cả 2 xe đều có tín hiệu xe nhan rẽ sang hướng khác nên cả 2 xe đều vi phạm quy tắc giao thông.', FALSE),
(5, 'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?', 'Xe con.', 'Xe tải.', 'Xe con, xe tải.', NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/191.png'), 'Xe con trong cả 2 phía đều đang đúng làn đường và đi theo hướng mà đèn xanh đang bật nên đúng quy tắc giao thông. Xe tải trong cả 2 phía đều ở sai làn đường so với hướng rẽ nên vi phạm quy tắc giao thông.', FALSE),
(5, 'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?', 'Xe tải, xe con.', 'Xe khách, xe con.', 'Xe khách, xe tải.', NULL, 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/192.png'), 'Xe con và xe mô tô đang ở đúng làn đường, đúng hướng rẽ với tín hiệu đèn xanh nên đúng quy tắc giao thông. Xe khách và xe tải đang ở sai làn đường so với hướng rẽ nên đều vi phạm.', FALSE),
(5, 'Các xe đi theo hướng mũi tên, xe nào vi phạm quy tắc giao thông?', 'Xe con, xe khách, xe tải.', 'Xe tải, xe khách, xe mô tô.', 'Xe khách, xe mô tô, xe con.', 'Cả bốn xe.', 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/193.png'), 'Xe con đi đúng làn đường theo hướng rẽ và tín hiệu đèn. Xe khách: Sai làn đường và tín hiệu đèn. Xe tải: Đi thẳng vi phạm đèn đỏ. Xe mô tô: Đi thẳng vi phạm đèn đỏ.', FALSE),
(5, 'Các xe đi theo hướng mũi tên, xe nào chấp hành đúng quy tắc giao thông?', 'Xe tải, mô tô.', 'Xe khách, mô tô.', 'Xe tải, xe con.', 'Mô tô, xe con.', 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/194.png'), 'Xe con và xe tải: Vi phạm tín hiệu đèn; Xe khách và xe mô tô: Đúng làn đường và đúng tín hiệu đèn.', FALSE),
(5, 'Các xe đi theo thứ tự nào là đúng quy tắc giao thông đường bộ?', 'Xe của bạn, mô tô, xe con.', 'Xe con, xe của bạn, mô tô.', 'Mô tô, xe con, xe của bạn.', NULL, 3, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/195.png'), 'Thứ tự ưu tiên: Xe ưu tiên – Đường ưu tiên – Đường cùng cấp: Bên phải trống, rẽ phải, đi thẳng, rẽ trái. Biển báo phía trước là đường ưu tiên và biển phụ báo hướng di chuyển của xe mô tô là hướng ưu tiên. 1. Xe mô tô: Đường ưu tiên; 2. Xe con: Đường không ưu tiên, bên phải trống; 3. Xe của bạn: Đường không ưu tiên, bên phải vướng xe con.', FALSE),
(5, 'Các xe đi theo thứ tự nào là đúng quy tắc giao thông đường bộ?', 'Xe của bạn, mô tô, xe con.', 'Xe con, xe của bạn, mô tô.', 'Mô tô, xe con, xe của bạn.', NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/196.png'), 'Thứ tự ưu tiên: Xe ưu tiên – Đường ưu tiên – Đường cùng cấp: Bên phải trống, rẽ phải, đi thẳng, rẽ trái. 1. Xe con: Rẽ phải; 2. Xe của bạn: Đi thẳng; 3. Mô tô: Rẽ trái.', FALSE),
(5, 'Bạn xử lý như thế nào trong trường hợp này?', 'Tăng tốc độ, rẽ phải trước xe tải và xe đạp.', 'Giảm tốc độ, rẽ phải sau xe tải và xe đạp.', 'Tăng tốc độ, rẽ phải trước xe đạp.', NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/197.png'), 'Xe tải đã vào nơi giao nhau trước nên sẽ được ưu tiên trước. Biển báo phía trước là đường ưu tiên dành cho xe thô sơ nên phải nhường đường cho xe đạp. Do đó, xe bạn rẽ phải sau xe tải và xe đạp.', FALSE),
(5, 'Xe nào dừng đúng theo quy tắc giao thông?', 'Xe con.', 'Xe mô tô.', 'Cả hai xe đều đúng.', NULL, 1, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/198.png'), 'Biển số W.211a “Giao nhau với đường sắt không có rào chắn” thì các phương tiện đứng cách xa đường sắt tối thiểu 5m nên xe con đúng.', FALSE),
(5, 'Xe của bạn đang di chuyển gần đến khu vực giao cắt với đường sắt, khi rào chắn đang di chuyển, bạn điều khiển xe như thế nào là đúng quy tắc giao thông?', 'Quan sát nếu thấy không có tàu thì tăng tốc cho xe vượt qua đường sắt.', 'Dừng lại trước rào chắn một khoảng cách an toàn.', 'Ra tín hiệu, yêu cầu người gác chắn tàu kéo chậm Barie để xe bạn qua.', NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/199.png'), 'Bắt buộc phải dừng xe.', FALSE),
(5, 'Trong tình huống dưới đây, xa đầu kéo kéo rơ moóc (xe container) đang rẽ phải, xe con màu xanh và xe máy phía sau xe container đi như thế nào để đảm bảo an toàn?', 'Vượt về phía bên phải để đi tiếp.', 'Giảm tốc độ chờ xe container rẽ xong rồi tiếp tục đi.', 'Vượt về phía bên trái để đi tiếp.', NULL, 2, LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/200.png'), 'Giảm tốc độ chờ xe đầu kéo rẽ phải rồi mới tiếp tục đi.', FALSE);