INSERT INTO gplx_app.road_sign_group (name) VALUES
('Biển báo cấm');
INSERT INTO gplx_app.road_sign (rs_group_id, name, description, image) VALUES
(1, 'Biển số P.124a "Cấm quay đầu xe"', 'Để báo cấm các loại xe quay đầu (theo kiểu chữ U). Chiều mũi tên phù hợp với chiều cấm quay đầu xe. Biển số P.124a có hiệu lực cấm các loại xe (cơ giới và thô sơ) trừ các xe được ưu tiên theo quy định. Biển không có giá trị cấm rẽ trái để đi sang hướng đường khác.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p124a.png')),
(1, 'Biển số P.124b "Cấm quay đầu xe"', 'Để báo cấm xe ô tô quay đầu (theo kiểu chữ U). Chiều mũi tên phù hợp với chiều cấm xe ô tô quay đầu. Biển số P.124b có hiệu lực cấm xe ô tô và xe máy 3 bánh (side car) quay đầu (theo kiểu chữ U) trừ các xe được ưu tiên theo quy định. Biển không có giá trị cấm rẽ trái để đi sang hướng đường khác.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p124b.png')),
(1, 'Biển số P.124c "Cấm rẽ trái và quay đầu xe"', 'Để báo cấm các loại xe rẽ trái đồng thời cấm quay đầu.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p124c.png')),
(1, 'Biển số P.124d "Cấm rẽ phải và quay đầu xe"', 'Để báo cấm các loại xe rẽ phải đồng thời cấm quay đầu.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p124d.png')),
(1, 'Biển số P.105 "Cấm xe ô tô và xe máy"', 'Để báo đường cấm các loại xe cơ giới và xe máy đi qua.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p105.png')),
(1, 'Biển số P.101 "Đường cấm"', 'Để báo đường cấm các loại phương tiện đi lại cả hai hướng, trừ các xe được ưu tiên theo quy định.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p101.png')),
(1, 'Biển số P.102 "Cấm đi ngược chiều"', 'Để báo đường cấm các loại xe (cơ giới và thô sơ) đi vào theo chiều đặt biển, trừ các xe được ưu tiên theo quy định. Người đi bộ được phép đi trên vỉa hè hoặc lề đường.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p102.png')),
(1, 'Biển số P.103a "Cấm xe ô tô"', 'Để báo đường cấm các loại xe cơ giới kể cả xe máy 3 bánh có thùng đi qua, trừ xe máy 2 bánh, xe gắn máy và các xe được ưu tiên theo quy định.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p103a.png')),
(1, 'Biển số P.103b "Cấm xe ô tô rẽ phải"', 'Cấm xe ô tô rẽ phải. Để báo đường cấm các loại xe cơ giới kể cả xe máy 3 bánh có thùng rẽ phải hay rẽ trái, trừ xe máy 2 bánh, xe gắn máy và các xe được ưu tiên theo quy định.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p103b.png')),
(1, 'Biển số P.103c "Cấm ô tô rẽ trái"', 'Cấm xe ô tô rẽ trái để báo đường cấm các loại xe cơ giới kể cả xe máy 3 bánh có thùng rẽ phải hay rẽ trái, trừ xe máy 2 bánh, xe gắn máy và các xe được ưu tiên theo quy định.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p103c.png')),
(1, 'Biển số P.104 "Cấm xe máy"', 'Để báo đường cấm các loại xe máy, trừ xe máy được ưu tiên theo quy định, phải đặt biển số P.104 "Cấm xe máy". Biển không có giá trị cấm những người dắt xe máy.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p104.png')),
(1, 'Biển số P.106a "Cấm xe ô tô tải"', 'Để báo đường cấm các loại xe ô tô tải trừ các xe được ưu tiên theo quy định, phải đặt biển số P.106a "Cấm xe ô tô tải". Biển có hiệu lực cấm đối với cả máy kéo và các xe máy chuyên dùng đi vào đoạn đường đặt biển số P.106a.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p106a.png')),
(1, 'Biển số P.106b "Cấm xe ô tô tải"', 'Để báo đường cấm các loại xe ô tô tải có khối lượng chuyên chở (theo Giấy chứng nhận kiểm định an toàn kỹ thuật và bảo vệ môi trường phương tiện giao thông cơ giới đường bộ) lớn hơn một giá trị nhất định, phải đặt biển số P.106b. Biển có hiệu lực cấm các xe ô tô tải có khối lượng chuyên chở (xác định theo Giấy chứng nhận kiểm định an toàn kỹ thuật và bảo vệ môi trường phương tiện giao thông cơ giới đường bộ) lớn hơn giá trị chữ số ghi trong biển (chữ số tấn ghi bằng màu trắng trên hình vẽ xe). Biển có hiệu lực cấm đối với cả máy kéo và các xe máy chuyên dùng đi vào đoạn đường đặt biển.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p106b.png')),
(1, 'Biển số P.106c "Cấm xe chở hàng nguy hiểm"', 'Để báo đường cấm các xe chở hàng nguy hiểm.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p106c.png')),
(1, 'Biển số P.107 "Cấm xe ô tô khách và xe ô tô tải"', 'Để báo đường cấm xe ô tô chở khách và các loại xe ô tô tải kể cả các loại máy kéo và xe máy thi công chuyên dùng đi qua trừ các xe được ưu tiên theo quy định.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p107.png')),
(1, 'Biển số P.107a "Cấm xe ô tô khách"', 'Để báo cấm ô tô chở khách đi qua trừ các xe ưu tiên theo quy định. Biển này không cấm xe buýt.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p107a.png')),
(1, 'Biển số P.107b "Cấm xe ô tô taxi"', 'Để báo cấm xe ô tô taxi đi lại. Trường hợp cấm xe ô tô taxi theo giờ thì đặt biển phụ ghi giờ cấm.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p107b.png')),
(1, 'Biển số P.108 "Cấm xe kéo rơ-moóc"', 'Để báo cấm các loại xe cơ giới kéo theo rơ-moóc kể cả xe máy, máy kéo, ô tô khách kéo theo rơ-moóc đi lại, trừ loại ô tô sơ-mi-rơ-moóc và các xe được ưu tiên (có kéo theo rơ-moóc) theo.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p108.png')),
(1, 'Biển số P.108a "Cấm xe sơ-mi-rơ-moóc"', 'Để báo đường cấm các loại xe sơ-mi-rơ-moóc và các xe kéo rơ-moóc trừ các xe được ưu tiên (có dạng xe sơ-mi-rơ-moóc hoặc có kéo theo rơ-moóc) theo quy định.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p108a.png')),
(1, 'Biển số P.109 "Cấm máy kéo"', 'Để báo đường cấm các loại máy kéo, kể cả máy kéo bánh hơi và bánh xích đi qua.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p109.png')),
(1, 'Biển số P.110a "Cấm xe đạp"', 'Để báo đường cấm xe đạp đi qua. Biển không có giá trị cấm những người dắt xe đạp.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p110a.png')),
(1, 'Biển số P.110b "Cấm xe đạp thồ"', 'Để báo đường cấm xe đạp thồ đi qua. Biển này không cấm người dắt loại xe này.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p110b.png')),
(1, 'Biển số P.111a "Cấm xe gắn máy"', 'Để báo đường cấm xe máy và xe gắn máy đi qua.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p111a.png')),
(1, 'Biển số P.111b "Cấm xe ba bánh loại có động cơ"', 'Để báo đường cấm xe ba bánh loại có động cơ như xe lam, xe xích lô máy, xe lôi máy, v.v...', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p111b.png')),
(1, 'Biển số P.111d "Cấm xe ba bánh loại không có động cơ"', 'Để báo đường cấm xe ba bánh loại không có động cơ như xe xích lô, xe lôi đạp, v.v...', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p111d.png')),
(1, 'Biển số P.112 "Cấm người đi bộ"', 'Để báo đường cấm người đi bộ qua lại.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p112.png')),
(1, 'Biển số P.113 "Cấm xe người kéo, đẩy"', 'Để báo đường cấm xe thô sơ, chuyển động do người kéo, đẩy đi qua. Biển không có giá trị cấm những xe nôi của trẻ em và phương tiện chuyên dùng để đi lại của những người tàn tật.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p113.png')),
(1, 'Biển số P.114 "Cấm xe súc vật kéo"', 'Để báo đường cấm súc vật vận tải hàng hóa hoặc hành khách dù kéo xe hay chở trên lưng đi qua.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p114.png')),
(1, 'Biển số P.115 "Hạn chế tải trọng toàn bộ xe"', 'Để báo đường cấm các loại xe (cơ giới và thô sơ) kể cả các xe được ưu tiên theo quy định, có tải trọng toàn bộ xe (tải trọng bản thân xe cộng với khối lượng chuyên chở) vượt quá trị số ghi trên biển đi qua.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p115.png')),
(1, 'Biển số P.116 "Hạn chế tải trọng trên trục xe"', 'Để báo đường cấm các loại xe (cơ giới và thô sơ) kể cả các xe được ưu tiên theo quy định, có tải trọng toàn bộ xe (cả xe và hàng) phân bố trên một trục bất kỳ của xe (tải trọng trục xe) vượt quá trị số ghi trên biển đi qua.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p116.png')),
(1, 'Biển số P.117 "Hạn chế chiều cao"', 'Để báo hạn chế chiều cao của xe.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p117.png')),
(1, 'Biển số P.118 "Hạn chế chiều ngang xe"', 'Để báo hạn chế chiều ngang của xe.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p118.png')),
(1, 'Biển số P.119 "Hạn chế chiều dài xe"', 'Để báo đường cấm các loại xe (cơ giới và thô sơ) kể cả các xe được ưu tiên theo quy định, có độ dài toàn bộ kể cả xe và hàng lớn hơn trị số ghi trên biển đi qua.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p119.png')),
(1, 'Biển số P.120 "Hạn chế chiều dài xe ôtô, máy kéo moóc hoặc..."', 'Để báo đường cấm các loại xe cơ giới kéo theo moóc kể cả ôtô sơ-mi-rơ-moóc và các loại xe được ưu tiên kéo moóc theo luật nhà nước quy định, có độ dài toàn bộ kể cả xe, moóc và hàng lớn hơn trị số ghi trên biển đi qua.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p120.png')),
(1, 'Biển số P.121 "Cự ly tối thiểu giữa hai xe"', 'Để bảo xe ô tô phải đi cách nhau một khoảng tối thiểu. Số ghi trên biển cho biết khoảng cách tối thiểu tính bằng mét. Biển có hiệu lực cấm các xe ô tô kể cả xe được ưu tiên theo quy định đi cách nhau một cự ly nhỏ hơn trị số ghi trên biển báo.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p121.png')),
(1, 'Biển số P.123a "Cấm rẽ trái"', 'Để báo cấm rẽ trái hoặc rẽ phải (theo hướng mũi tên chỉ) ở những vị trí đường giao nhau. Biển không có giá trị cấm quay đầu xe. Biển có hiệu lực cấm các loại xe (cơ giới và thô sơ) rẽ sang phía trái trừ các xe được ưu tiên theo quy định.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p123a.png')),
(1, 'Biển số P.123b "Cấm rẽ phải"', 'Để báo cấm rẽ trái hoặc rẽ phải (theo hướng mũi tên chỉ) ở những vị trí đường giao nhau. Biển không có giá trị cấm quay đầu xe. Biển có hiệu lực cấm các loại xe (cơ giới và thô sơ) rẽ sang phía phải trừ các xe được ưu tiên theo quy định.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p123b.png')),
(1, 'Biển số P.126 "Cấm ô tô tải vượt"', 'Để báo cấm các loại ô tô tải vượt xe cơ giới khác, phải đặt biển số P.126 "Cấm ô tô tải vượt". Biển có hiệu lực cấm các loại ô tô tải có khối lượng chuyên chở (theo Giấy chứng nhận kiểm định an toàn kỹ thuật và bảo vệ môi trường phương tiện giao thông cơ giới đường bộ) lớn hơn 3.500 kg kể cả các xe được ưu tiên theo quy định vượt xe cơ giới khác. Được phép vượt xe máy 2 bánh, xe gắn máy. Biển không có giá trị cấm các loại xe cơ giới khác vượt nhau và vượt ô tô tải.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p126.png')),
(1, 'Biển số P.127 "Tốc độ tối đa cho phép"', 'Để báo tốc độ tối đa cho phép các xe cơ giới chạy.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p127.png')),
(1, 'Biển số P.127a "Tốc độ tối đa cho phép về ban đêm"', 'Khi cần phải quy định tốc độ tối đa về ban đêm cho các phương tiện.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p127a.png')),
(1, 'Biển số P.127b "Biển ghép tốc độ tối đa cho phép trên từng làn..."', 'Khi quy định tốc độ tối đa cho phép trên từng làn đường. Xe chạy trên làn nào phải tuân thủ tốc độ tối đa cho phép trên làn đường đó.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p127b.png')),
(1, 'Biển số P.128 "Cấm sử dụng còi"', 'Để báo cấm các loại xe sử dụng còi.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p128.png')),
(1, 'Biển số P.129 "Kiểm tra"', 'Để báo nơi đặt trạm kiểm tra; các loại phương tiện vận tải qua đó phải dừng lại để làm thủ tục kiểm tra, kiểm soát theo quy định.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p129.png')),
(1, 'Biển số P.130 "Cấm dừng xe và đỗ xe"', 'Để báo nơi cấm dừng xe và đỗ xe.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p130.png')),
(1, 'Biển số P.131a "Cấm đỗ xe"', 'Để báo nơi cấm đỗ xe trừ các xe được ưu tiên theo quy định. Biển số P.131a có hiệu lực cấm các loại xe cơ giới đỗ ở phía đường có đặt biển.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p131a.png')),
(1, 'Biển số P.131b "Cấm đỗ xe"', 'Để báo nơi cấm đỗ xe trừ các xe được ưu tiên theo quy định. Biển số P.131b có hiệu lực cấm các loại xe cơ giới đỗ ở phía đường có đặt biển vào những ngày lẻ.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p131b.png')),
(1, 'Biển số P.131c "Cấm đỗ xe"', 'Để báo nơi cấm đỗ xe trừ các xe được ưu tiên theo quy định. Biển số P.131c có hiệu lực cấm các loại xe cơ giới đỗ ở phía đường có đặt biển vào những ngày chẵn.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p131c.png')),
(1, 'Biển số P.132 "Nhường đường cho xe cơ giới đi ngược chiều qu..."', 'Để báo các loại xe (cơ giới và thô sơ) kể cả các xe được ưu tiên theo quy định khi thấy biển số P.132 phải nhường đường cho các loại xe cơ giới đang đi theo hướng ngược lại qua các đoạn đường hẹp hoặc cầu hẹp.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p132.png')),
(1, 'Biển số DP.133 "Hết cấm vượt"', 'Để báo hết đoạn đường cấm vượt. Biển có tác dụng báo cho người tham gia giao thông biết hiệu lực của các biển số P.125 và biển số P.126 hết tác dụng. Các xe cơ giới được phép vượt nhau nhưng phải theo quy định về điều kiện cho phép vượt nhau.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/dp133.png')),
(1, 'Biển số DP.134 "Hết hạn chế tốc độ tối đa"', 'Đến hết đoạn đường hạn chế tốc độ tối đa.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/dp134.png')),
(1, 'Biển số DP.135 "Hết tất cả các lệnh cấm"', 'Đến hết đoạn đường mà nhiều biển báo cấm cùng hết hiệu lực.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/dp135.png')),
(1, 'Biển số P.136 "Cấm đi thẳng"', 'Đường ở phía trước cấm tất cả các loại xe (cơ giới và thô sơ) đi thẳng.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p136.png')),
(1, 'Biển số P.137 "Cấm rẽ trái, rẽ phải"', 'Các ngả đường phía trước cấm tất cả các loại xe (cơ giới và thô sơ) rẽ trái hay rẽ phải.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p137.png')),
(1, 'Biển số P.138 "Cấm đi thẳng, rẽ trái"', 'Biểu thị đường qua nút giao cấm tất cả các loại xe (cơ giới và thô sơ) đi thẳng, rẽ trái.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p138.png')),
(1, 'Biển số P.139 "Cấm đi thẳng, rẽ phải"', 'Biểu thị đường qua nút giao cấm tất cả các loại xe (cơ giới và thô sơ) đi thẳng, rẽ phải.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p139.png')),
(1, 'Biển số P.140 "Cấm xe công nông và các loại xe tương tự"', 'Để báo đường cấm xe công nông.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/p140.png'));
