INSERT INTO gplx_app.road_sign (rs_group_id, name, description, image) VALUES
(2, 'Biển số R.122 "Dừng lại"', 'Để báo các xe (cơ giới và thô sơ) dừng lại.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r122.png')),
(2, 'Biển số R.301a "Hướng đi phải theo"', 'Khi đặt biển số R.301a ở trước nơi đường giao nhau thì hiệu lực tác dụng của biển là ở phạm vi khu vực nơi đường giao nhau phía sau biển tức là cấm rẽ trái hay rẽ trái. Nếu biển này đặt ở sau nơi đường giao nhau (bắt đầu vào đoạn đường phụ) thì hiệu lực tác dụng của biển là từ vị trí đặt biển đến nơi đường giao nhau. Trong trường hợp này cấm rẽ trái và quay đầu trong vùng tác dụng của biển, chỉ cho phép rẽ phải vào cổng nhà hoặc ngõ phố có trên đoạn đường từ nơi đường giao nhau đặt biển đến nơi đường giao nhau tiếp theo.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r301a.png')),
(2, 'Biển số R.301b "Hướng đi phải theo"', 'Nhằm chỉ hướng cho phép xe đi ngang qua nơi đường giao nhau và ngăn chặn chiều đi ngược chiều trên đường phố với đường một chiều. Biển bắt buộc người tham gia giao thông chỉ được phép rẽ phải hoặc rẽ trái ở phạm vi nơi đường giao nhau trước mặt biển.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r301b.png')),
(2, 'Biển số R.301c "Hướng đi phải theo"', 'Nhằm chỉ hướng cho phép xe đi ngang qua nơi đường giao nhau và ngăn chặn hướng đi ngược chiều trên đường phố với đường một chiều. Biển bắt buộc 1 người tham gia giao thông chỉ được phép rẽ phải hoặc rẽ trái ở phạm vi nơi đường giao nhau trước mặt biển.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r301c.png')),
(2, 'Biển số R.301d "Hướng đi phải theo"', 'Biển bắt buộc người tham gia giao thông chỉ được phép rẽ phải ở phạm vi nơi đường giao nhau đằng sau mặt biển.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r301d.png')),
(2, 'Biển số R.301e "Hướng đi phải theo"', 'Biển bắt buộc người tham gia giao thông chỉ được phép rẽ trái ở phạm vi nơi đường giao nhau đằng sau mặt biển.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r301e.png')),
(2, 'Biển số R.301f "Hướng đi phải theo"', 'Biển bắt buộc người tham gia giao thông chỉ được phép đi thẳng hay rẽ phải ở phạm vi nơi đường giao nhau đằng sau mặt biển.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r301f.png')),
(2, 'Biển số R.301h "Hướng đi phải theo"', 'Người tham gia giao thông chỉ được phép đi thẳng hay rẽ trái và được phép quay đầu xe để đi theo hướng ngược lại.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r301h.png')),
(2, 'Biển số R.301i "Hướng đi phải theo"', 'Nhằm ngăn ngừa chuyển động ngược chiều trên đường phố với đường một chiều. Biển bắt buộc người tham gia giao thông chỉ được phép đi thẳng, quay đầu hoặc rẽ trái ở phạm vi nơi đường giao nhau trước mặt biển.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r301i.png')),
(2, 'Biển số R.302a "Hướng phải đi vòng chướng ngại vật"', 'Để báo các loại xe (cơ giới và thô sơ) hướng đi để qua một chướng ngại vật. Chỉ được vòng sang phải.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r302a.png')),
(2, 'Biển số R.302b "Hướng phải đi vòng chướng ngại vật"', 'Để báo các loại xe (cơ giới và thô sơ) hướng đi để qua một chướng ngại vật. Chỉ được vòng sang trái.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r302b.png')),
(2, 'Biển số R.302c "Hướng phải đi vòng chướng ngại vật"', 'Để báo các loại xe (cơ giới và thô sơ) hướng đi để qua một chướng ngại vật. Phải vòng sang trái hoặc sang phải mà lựa chọn kiểu biển cho phù hợp.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r302c.png')),
(2, 'Biển số R.303 "Nơi giao nhau chạy theo vòng xuyến"', 'Để báo cho các loại xe (thô sơ và cơ giới) phải chạy vòng theo đảo an toàn ở nơi đường giao nhau. Biển có hiệu lực bắt buộc các loại xe muốn chuyển hướng phải chạy vòng theo đảo an toàn theo chiều mũi tên.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r303.png')),
(2, 'Biển số R.304 "Đường dành cho xe thô sơ"', 'Để báo đường dành cho xe thô sơ (kể cả xe của người tàn tật) và người đi bộ. Biển có hiệu lực bắt buộc các loại xe thô sơ (kể cả xe của người tàn tật) và người đi bộ phải dùng đường dành riêng này để đi và cấm các xe cơ giới (kể cả xe gắn máy, các xe được ưu tiên theo quy định) đi vào đường đặt biển này, trừ trường hợp đi cắt ngang qua nhưng phải đảm bảo tuyệt đối an toàn cho xe thô sơ và người đi bộ.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r304.png')),
(2, 'Biển số R.305 "Đường dành cho người đi bộ"', 'Để báo đường dành cho người đi bộ. Biển có hiệu lực bắt buộc người đi bộ phải dùng đường dành riêng này để đi. Các loại xe cơ giới và thô sơ (trừ xe đạp và xe lăn dành cho người tàn tật), kể cả các xe được ưu tiên theo quy định không được phép đi vào trên đường đã đặt biển này, trừ trường hợp đi cắt ngang qua nhưng phải đảm bảo tuyệt đối an toàn cho người đi bộ.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r305.png')),
(2, 'Biển số R.306 "Tốc độ tối thiểu cho phép"', 'Để báo tốc độ tối thiểu cho phép các xe cơ giới chạy. Biển có hiệu lực bắt buộc các loại xe cơ giới vận hành với tốc độ không nhỏ hơn trị số ghi trên biển trong điều kiện giao thông thuận lợi và an toàn. Các loại xe có tốc độ tối đa theo quy định của nhà sản xuất không đạt tốc độ tối thiểu đã ghi trên biển không được phép đi vào đường này. Trị số ghi trên biển chỉ tốc độ tối thiểu cho phép tính bằng km/h và được quy định tùy theo tiêu chuẩn kỹ thuật của cầu đường và tổ chức giao thông, không được quy định trị số lớn hơn trị số tốc độ an toàn.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r306.png')),
(2, 'Biển số R.307 "Hết hạn chế tốc độ tối thiểu"', 'Biển có giá trị báo cho người tham gia giao thông biết hiệu lực của biển số R.306 hết tác dụng. Kể từ biển này các xe được phép chạy chậm hơn trị số ghi trên biển nhưng không được gây cản trở các xe khác.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r307.png')),
(2, 'Biển số R.308a "Tuyển đường cầu vượt cắt qua"', 'Biểu thị phía trước có cầu vượt, xe có thể đi thẳng hoặc theo chỉ dẫn trên biển để rẽ trái.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r308a.png')),
(2, 'Biển số R.308b "Tuyển đường cầu vượt cắt qua"', 'Biểu thị phía trước có cầu vượt, xe có thể đi thẳng hoặc theo chỉ dẫn trên hình vẽ để rẽ phải.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r308b.png')),
(2, 'Biển số R.309 "Ấn còi"', 'Biểu thị xe cộ đi đến vị trí cắm biển đó thì phải ấn còi.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r309.png')),
(2, 'Biển số R.310a "Hướng đi phải theo cho các xe chở hàng nguy hiểm"', 'Để báo cho các loại xe chở hàng nguy hiểm phải đi theo hướng quy định. Xe bắt buộc phải rẽ trái.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r310a.png')),
(2, 'Biển số R.310b "Hướng đi phải theo cho các xe chở hàng nguy hiểm"', 'Để báo cho các loại xe chở hàng nguy hiểm phải đi theo hướng quy định. Xe bắt buộc phải đi thẳng.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r310b.png')),
(2, 'Biển số R.310c "Hướng đi phải theo cho các xe chở hàng nguy hiểm"', 'Để báo cho các loại xe chở hàng nguy hiểm phải đi theo hướng quy định. Xe bắt buộc rẽ phải.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r310c.png')),
(2, 'Biển số R.403a "Đường dành cho xe ô tô"', 'Để báo hiệu bắt đầu đường dành cho các loại ô tô đi lại.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r403a.png')),
(2, 'Biển số R.403b "Đường dành cho xe ô tô, xe máy"', 'Để báo hiệu bắt đầu đường dành cho các loại ô tô, xe máy (kể cả xe gắn máy) đi lại.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r403b.png')),
(2, 'Biển số R.403c "Đường dành cho xe buýt"', 'Để báo hiệu bắt đầu đường dành cho xe buýt.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/r403c.png')),
