INSERT INTO gplx_app.road_sign (rs_group_id, name, description, image) VALUES
(5, 'Biển số S.501 "Phạm vi tác dụng của biển"', 'Để thông báo chiều dài đoạn đường nguy hiểm hoặc cấm hoặc hạn chế bên dưới một số biển báo nguy hiểm, biển báo cấm hoặc hạn chế.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s501.png')),
(5, 'Biển số S.502 "Khoảng cách đến đối tượng báo hiệu"', 'Để thông báo khoảng cách thực tế từ vị trí đặt biển đến đối tượng báo hiệu ở phía trước. Con số trên biển ghi theo đơn vị mét (m) và lấy chẵn đến hàng chục mét.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s502.png')),
(5, 'Biển số S.503a "Hướng tác dụng của biển"', 'Để chỉ hướng tác dụng của biển là hướng vuông góc với chiều đi.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s503a.png')),
(5, 'Biển số S.503b "Hướng tác dụng của biển"', 'Để chỉ đồng thời hai hướng tác dụng của biển (trái và phải).', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s503b.png')),
(5, 'Biển số S.503c "Hướng tác dụng của biển"', 'Để chỉ hướng tác dụng của biển là hướng vuông góc với chiều đi.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s503c.png')),
(5, 'Biển số S.503d "Hướng tác dụng của biển"', 'Để chỉ hướng tác dụng của biển là hướng song song với chiều đi.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s503d.png')),
(5, 'Biển số S.503e "Hướng tác dụng của biển"', 'Để chỉ đồng thời hai hướng tác dụng (trước và sau) nơi đặt biển nhắc lại lệnh cấm dừng và cấm đỗ xe.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s503e.png')),
(5, 'Biển số S.503f "Hướng tác dụng của biển"', 'Để chỉ hướng tác dụng của biển là hướng song song với chiều đi.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s503.png')),
(5, 'Biển số S.504 "Làn đường"', 'Để chỉ làn đường chịu hiệu lực của biển báo hay đèn tín hiệu.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s504.png')),
(5, 'Biển số S.505a "Loại xe"', 'Để chỉ loại xe chịu hiệu lực của biển báo cấm, biển hiệu lệnh hay biển chỉ dẫn.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s505a.png')),
(5, 'Biển số S.505b "Loại xe hạn chế qua cầu"', 'Để chỉ các loại xe tải chịu hiệu lực của biển báo có tải trọng toàn bộ xe được phép (bao gồm tải trọng bản thân xe và khối lượng chuyên chở cho phép) tương ứng với mỗi loại xe không phụ thuộc vào số lượng trục.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s505b.png')),
(5, 'Biển số S.505c "Tải trọng trục hạn chế qua cầu"', 'Để chỉ các loại xe tải có tải trọng trục lớn nhất cho phép tương ứng với mỗi loại trục (trục đơn, trục kép, trục ba).', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s505c.png')),
(5, 'Biển số S.506a "Hướng đường ưu tiên"', 'Để chỉ dẫn cho người tham gia giao thông trên đường này biết hướng đường ưu tiên ở ngã tư.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s506a.png')),
(5, 'Biển số S.506b "Hướng đường ưu tiên"', 'Để chỉ dẫn cho người tham gia giao thông trên đường này biết hướng đường ưu tiên ở ngã tư.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s506b.png')),
(5, 'Biển số S.508a "Biểu thị thời gian"', 'Nhằm quy định phạm vi thời gian hiệu lực của các biển báo cấm, biển hiệu lệnh cho phù hợp yêu cầu.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s508a.png')),
(5, 'Biển số S.508b "Biểu thị thời gian"', 'Nhằm quy định phạm vi thời gian hiệu lực của các biển báo cấm, biển hiệu lệnh cho phù hợp yêu cầu.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s508b.png')),
(5, 'Biển số S.509a "Thuyết minh biển chính"', 'Để bổ sung cho biển số W.239 "Đường cáp điện ở phía trên", biển này ghi rõ chiều cao cho các phương tiện đi qua an toàn.', LOAD_FILE('C:/ProgramData/MySQL/MySQL Server 8.0/Uploads/bienbao/s509a.png')),
